import React, {useEffect, useState} from 'react';
import {useDispatch, useSelector} from "react-redux";
import {CButton, CCol, CNav, CRow, CTabContent, CTabs} from "@coreui/react";
import MedicalTestTabHeader from "./MedicalTestTabHeader";
import MedicalTestTabBody from "./MedicalTestTabBody";
import Constants from "../../../../../../../../../utils/Constants";
import * as Action from "../store/actions/patient.add.medical.test.results.action";

const generateSaveObject = (checkupData, patientMedicalTestsData) => {
    return Object.assign({}, {
        checkupID: checkupData.checkup ? checkupData.checkup.checkupID : null,
        medicalTestResults: patientMedicalTestsData.formData
    });
};

const MedicalTestList = () => {

    let dispatch = useDispatch();
    const checkupData = useSelector(({checkup}) => checkup.checkupAddEdit);
    const patientMedicalTestsData = useSelector(({patientMedicalTest}) => patientMedicalTest.patientMedicalTestAddEdit);
    const [activeTabID, setActiveTabID] = useState();

    useEffect(() => {
        setActiveTabID(patientMedicalTestsData.medicalTestTypes[0] ? patientMedicalTestsData.medicalTestTypes[0].medicalTestTypeID : 0);
    }, [patientMedicalTestsData.medicalTestTypes]);

    const save = () => {
        let saveObj = generateSaveObject(checkupData, patientMedicalTestsData);
        if (saveObj.checkupID) {
            dispatch(Action.saveOrUpdateCheckupWithPatientMedicalTestsResults(saveObj));
        }
    };


    return (
        <div>
            <CRow>
                <CCol sm="12" md="6" lg="5">
                    <div  style={{height:500,overflowY:'scroll',overflowX:'hidden'}} >
                                    <div style={{backgroundColor:`#e57373`,height:500}} id="mone">
                                        <CRow style={{paddingTop:'10px'}}>
                                            <CCol sm="12" md="6" lg="5" >
                                                <h4 style={{textAlign: 'right'}}>Examination</h4> 
                                            </CCol>
                                        </CRow>
                                        <CRow >
                                            <CCol sm="12" md="6" lg="5" >
                                                <h5 style={{textAlign: 'right'}}>Height</h5> 
                                            </CCol>
                                            <CCol sm="12" md="6" lg="3" >
                                                <CFormGroup>
                                                    <CInput
                                                        type="text"
                                                        name={'value'}
                                                        placeholder={'Value'}
                                                        style={{height:'25px'}}
                                                    />
                                                </CFormGroup>
                                            </CCol>
                                            <CCol sm="12" md="6" lg="4" >
                                                {/* <CFormGroup>
                                                    <CLabel htmlFor="bloodGroup">Blood
                                                        Group</CLabel>

                                                    <Field name="bloodGroup"
                                                        //    className={getInputFieldClassNames(touched.bloodGroup, errors.bloodGroup)}
                                                    >
                                                        {({
                                                              field, // { name, value, onChange, onBlur }
                                                              form: {touched, errors}, // also values, setXXXX, handleXXXX, dirty, isValid, status, etc.
                                                              meta,
                                                          }) => (

                                                            <CSelect custom name="bloodGroup" {...field}>
                                                                {
                                                                    Constants.BLOOD_GROUP.map((item) => {
                                                                        return (
                                                                            <option key={item.key}
                                                                                    value={item.key}>{item.value}</option>
                                                                        );
                                                                    })
                                                                }
                                                            </CSelect>
                                                        )}
                                                    </Field>
                                                </CFormGroup> */}
                                            </CCol>
                                        </CRow>
                                        <CRow>
                                            <CCol sm="12" md="6" lg="5" >
                                                <p style={{textAlign: 'right'}}>Examination</p> 
                                            </CCol>
                                        </CRow>
                                    </div>

                                    <div style={{backgroundColor:`#f06292`,height:500}} id="mtwo">
                                        <p >Blood Pressure</p> 
                                    </div>

                                    <div style={{backgroundColor:`#fff176`,height:500}} id="mthree">
                                        <p >Glycaemic Control</p> 
                                    </div>
                            </div>
                        </CCol>
                        <CCol sm="12" md="6" lg="7">
                            <div className='medicalTestTypeMenu'>
                                <div style={{backgroundColor:`#e57373`}} onClick={()=>document.getElementById('mone').scrollIntoView()} className='medicalTestTypeCard'  >
                                    <a >Examination</a> 
                                </div>
                                <div style={{backgroundColor:`#f06292`}} onClick={()=>document.getElementById('mtwo').scrollIntoView()} className='medicalTestTypeCard'  >
                                    <a >Blood Pressure</a> 
                                </div>
                                <div style={{backgroundColor:`#fff176`}} onClick={()=>document.getElementById('mthree').scrollIntoView()} className='medicalTestTypeCard'>
                                    <p >Glycaemic Control</p> 
                                </div>
                                <div style={{backgroundColor:`#dce775`}} className='medicalTestTypeCard'>
                                    <p >Kidney Function</p> 
                                </div>
                                <div style={{backgroundColor:`#4dd0e1`}} className='medicalTestTypeCard'>
                                    <p >Bone</p> 
                                </div>
                                <div style={{backgroundColor:`#ffb74d`}} className='medicalTestTypeCard'>
                                    <p >Cardiac Assessment</p> 
                                </div>
                                <div style={{backgroundColor:`#64b5f6`}} className='medicalTestTypeCard'>
                                    <p >Thyroid Hermone</p> 
                                </div>
                                <div style={{backgroundColor:`#a1887f`}} className='medicalTestTypeCard'>
                                    <p >Lipid Profile</p> 
                                </div>
                            </div>

                        </CCol>
                    </CRow>

        </div>
    );
};

export default MedicalTestList;
