import React, {useEffect, useState} from 'react';
import {useDispatch, useSelector} from "react-redux";
import { CCol, CRow, CFormGroup, CInput,CSelect} from "@coreui/react";

import * as Action from "../store/actions/patient.add.medical.test.results.action";

const generateSaveObject = (checkupData, patientMedicalTestsData) => {
    return Object.assign({}, {
        checkupID: checkupData.checkup ? checkupData.checkup.checkupID : null,
        medicalTestResults: patientMedicalTestsData.formData
    });
};

const colors = ['#','#','#','#','#','#','#','#','#','#','#'];

const MedicalTestList = () => {

    let dispatch = useDispatch();
    const checkupData = useSelector(({checkup}) => checkup.checkupAddEdit);
    const patientMedicalTestsData = useSelector(({patientMedicalTest}) => patientMedicalTest.patientMedicalTestAddEdit);

    const save = () => {
        let saveObj = generateSaveObject(checkupData, patientMedicalTestsData);
        if (saveObj.checkupID) {
            dispatch(Action.saveOrUpdateCheckupWithPatientMedicalTestsResults(saveObj));
        }
    };


    return (
        <div>
            <CRow>
                <CCol sm="12" md="6" lg="5">
                    <div  style={{overflowY:'scroll',height:'500px',overflowX:'hidden'}} >
                        {   
                        patientMedicalTestsData.medicalTestTypes.map((medicalTestType, indexr) =>
                            <div key={indexr} style={{backgroundColor:`#e57373`,marginBottom:'20px'}} id="mone">
                                <CRow style={{paddingTop:'10px'}}>
                                    <CCol sm="12" md="6" lg="6" >
                                        <h4 style={{textAlign: 'right'}}>{medicalTestType.testType}</h4> 
                                    </CCol>
                                </CRow>
                                {
                                    medicalTestType.medicalTestRecords.map((record,index)=>
                                        <CRow key={index} >
                                            <CCol sm="12" md="6" lg="5" >
                                                <h6 style={{textAlign: 'right',paddingTop:'10px'}}>{record.measurementName}</h6> 
                                            </CCol>
                                            <CCol sm="12" md="6" lg="3" >
                                                <CFormGroup>
                                                    <CInput
                                                        type="text"
                                                        name={'value'}
                                                        placeholder={'Value'}
                                                        style={{height:'35px'}}
                                                    />
                                                </CFormGroup>
                                            </CCol>

                                            <CCol sm="12" md="6" lg="3" >
                                                <CFormGroup>
                                                    <CSelect custom name="status" style={{height:'35px'}} >
                                                        {
                                                            record.measurementUnits.map((item) => {
                                                                return (
                                                                    <option key={item} value={item}>{item}</option>
                                                                );
                                                            })
                                                        }
                                                    </CSelect>
                                                </CFormGroup> 
                                            </CCol>
                                        </CRow>
                                    )
                                }

                            </div>
                        )
                        }
                    </div>
                </CCol>

                <CCol sm="12" md="6" lg="7">
                    <div className='medicalTestTypeMenu'>
                        <div style={{backgroundColor:`#e57373`}} onClick={()=>document.getElementById('mone').scrollIntoView()} className='medicalTestTypeCard'  >
                            <a >Examination</a> 
                        </div>
                        <div style={{backgroundColor:`#f06292`}} onClick={()=>document.getElementById('mtwo').scrollIntoView()} className='medicalTestTypeCard'  >
                            <a >Blood Pressure</a> 
                        </div>
                        <div style={{backgroundColor:`#fff176`}} onClick={()=>document.getElementById('mthree').scrollIntoView()} className='medicalTestTypeCard'>
                            <p >Glycaemic Control</p> 
                        </div>
                        <div style={{backgroundColor:`#dce775`}} onClick={()=>console.log(patientMedicalTestsData.medicalTestTypes[3])}  className='medicalTestTypeCard'>
                            <p >Kidney Function</p> 
                        </div>
                        <div style={{backgroundColor:`#4dd0e1`}} className='medicalTestTypeCard'>
                            <p >Bone</p> 
                        </div>
                        <div style={{backgroundColor:`#ffb74d`}} className='medicalTestTypeCard'>
                            <p>Cardiac Assessment</p> 
                        </div>
                        <div style={{backgroundColor:`#64b5f6`}} className='medicalTestTypeCard'>
                            <p>Thyroid Hermone</p> 
                        </div>
                        <div style={{backgroundColor:`#a1887f`}} className='medicalTestTypeCard'>
                            <p>Lipid Profile</p> 
                        </div>
                    </div>
                </CCol>
            </CRow>
        </div>
    );
};

export default MedicalTestList;
